con:config.script:
import static javax.swing.JOptionPane.showConfirmDialog
import javax.swing.JOptionPane

def message = "Warning: only use this function if you want to revert back all the EB LCM updates you did and restore to the default data set for testing!\nWarning: make sure that the correct countryCode is set in the Custom Properties at SoapUI project level (OOTS-CS-LCMInterface). Otherwise, you will interfere with other test teams!"
def result = showConfirmDialog(null, message)
if (result == JOptionPane.YES_OPTION)
{
  log.info "Continuing the restore."
} else if (result == JOptionPane.NO_OPTION)
{
  log.info "Aborting the restore."
  testRunner.fail("Aborting the restore.")
} else if (result == JOptionPane.CANCEL_OPTION)
{
  log.info "Aborting the restore."
  testRunner.fail("Aborting the restore.")
}

